import React, { useState } from "react";

const GradCAMVisualization = ({ visualizations, isLoading }) => {
  const [imageError, setImageError] = useState(false);
  const [selectedOverlay, setSelectedOverlay] = useState("main");

  // Handle different formats of visualization data
  const getVisualizationUrl = (data) => {
    if (!data) return null;

    // If it's already a complete data URL
    if (typeof data === "string" && data.startsWith("data:image")) {
      return data;
    }

    // If it's a base64 string
    if (typeof data === "string" && data.match(/^[A-Za-z0-9+/=]+$/)) {
      return `data:image/png;base64,${data}`;
    }

    // If the data is invalid, return null
    return null;
  };

  const handleImageError = () => {
    setImageError(true);
    console.error("Failed to load Grad-CAM visualization");
  };

  const mainVisualization = visualizations?.main;
  const overlays = visualizations?.overlays || {};

  // Check if the visualization data is valid
  const isValidVisualization = (data) => {
    return data && !imageError;
  };

  return (
    <div className="mt-4">
      <h3 className="text-lg font-semibold mb-2 text-blue-800">
        Grad-CAM Visualization
      </h3>
      <div className="border rounded-lg shadow-sm bg-white overflow-hidden">
        {isLoading ? (
          <div className="h-64 flex items-center justify-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
          </div>
        ) : (
          <div className="p-2 space-y-2">
            <div className="flex justify-center items-center min-h-[256px]">
              {isValidVisualization(mainVisualization) ? (
                <div className="relative w-full h-64">
                  <img
                    src={getVisualizationUrl(
                      selectedOverlay === "main"
                        ? mainVisualization
                        : overlays[selectedOverlay]
                    )}
                    alt="Grad-CAM heatmap highlighting regions of interest"
                    className="w-full h-full object-contain rounded"
                    onError={handleImageError}
                    aria-label="Grad-CAM Visualization"
                  />
                  <div className="absolute bottom-2 right-2 bg-black bg-opacity-50 text-white text-xs px-2 py-1 rounded">
                    Grad-CAM Overlay
                  </div>
                </div>
              ) : (
                <div className="text-center py-8 px-4">
                  <p className="text-gray-500 text-sm mb-2">
                    {imageError
                      ? "Failed to load Grad-CAM visualization"
                      : "No Grad-CAM visualization available"}
                  </p>
                  {imageError && (
                    <button
                      onClick={() => setImageError(false)}
                      className="text-blue-600 text-sm hover:text-blue-800"
                      aria-label="Retry loading Grad-CAM visualization"
                    >
                      Retry
                    </button>
                  )}
                </div>
              )}
            </div>

            {/* Overlay Selection */}
            {Object.keys(overlays).length > 0 && (
              <div className="flex justify-center gap-2">
                <button
                  onClick={() => setSelectedOverlay("main")}
                  className={`px-3 py-1 rounded text-sm ${
                    selectedOverlay === "main"
                      ? "bg-blue-600 text-white"
                      : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                  }`}
                  aria-label="Show combined overlay"
                >
                  Combined
                </button>
                {Object.keys(overlays).map((opacity) => (
                  <button
                    key={opacity}
                    onClick={() => setSelectedOverlay(opacity)}
                    className={`px-3 py-1 rounded text-sm ${
                      selectedOverlay === opacity
                        ? "bg-blue-600 text-white"
                        : "bg-gray-100 text-gray-700 hover:bg-gray-200"
                    }`}
                    aria-label={`Show ${parseInt(opacity * 100)}% overlay`}
                  >
                    {`${parseInt(opacity * 100)}% Overlay`}
                  </button>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
      {isValidVisualization(mainVisualization) && (
        <p className="text-xs text-gray-500 mt-2">
          This visualization highlights the regions that influenced the model's
          decision.
        </p>
      )}
    </div>
  );
};

export default GradCAMVisualization;
