import React, { useState } from 'react';
import { Upload, RefreshCw } from 'lucide-react';
import { motion } from 'framer-motion';
import axios from 'axios'; // Add this import

const ClassificationPage = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedModel, setSelectedModel] = useState('mobilenet_v3');
  const [error, setError] = useState(null);

  const models = [
    { value: 'mobilenet_v3', label: 'MobileNet V3', description: 'Lightweight and fast, ideal for mobile devices.' },
    { value: 'efficientnet_b0', label: 'EfficientNet B0', description: 'Balanced accuracy and efficiency.' },
    { value: 'resnet18', label: 'ResNet 18', description: 'Deep residual learning for image recognition.' },
    { value: 'resnet50', label: 'ResNet 50', description: 'Improved accuracy with deeper architecture.' },
    { value: 'squeezenet1_1', label: 'SqueezeNet', description: 'Compact model with minimal parameters.' },
    { value: 'mobilevit', label: 'MobileViT', description: 'Combines CNNs and Transformers for mobile devices.' },
  ];

 // ... (keep the imports and initial part of the code)

const classifyImage = async (file, modelName) => {
  const formData = new FormData();
  formData.append('file', file);
  formData.append('model_name', modelName);

  try {
    const response = await axios.post('http://localhost:5000/api/classify', formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
      // Add timeout and retry configuration
      timeout: 30000, // 30 seconds timeout
      maxRetries: 3,
    });
    
    if (response.data.error) {
      throw new Error(response.data.error);
    }
    
    return response.data;
  } catch (error) {
    console.error('Classification error:', error);
    if (error.response) {
      throw new Error(error.response.data.error || 'Server error');
    } else if (error.request) {
      throw new Error('Network error - please check your connection');
    } else {
      throw new Error('Error processing image');
    }
  }
};

const handleImageUpload = async (event) => {
  const file = event.target.files[0];
  if (!file) return;

  // Validate file type
  if (!file.type.startsWith('image/')) {
    setError('Please upload an image file');
    return;
  }

  // Validate file size (e.g., max 5MB)
  if (file.size > 5 * 1024 * 1024) {
    setError('Image size should be less than 5MB');
    return;
  }

  setSelectedImage(URL.createObjectURL(file));
  setIsLoading(true);
  setError(null);

  try {
    const result = await classifyImage(file, selectedModel);
    setPrediction(result);
  } catch (error) {
    console.error('Error:', error);
    setError(error.message);
  } finally {
    setIsLoading(false);
  }
};

// ... (keep the rest of the component code)

  const handleReset = () => {
    setSelectedImage(null);
    setPrediction(null);
    setError(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-200 via-blue-100 to-blue-50 bg-cover bg-center">
      <div className="max-w-5xl mx-auto p-8">
        <motion.h1
          className="text-4xl font-extrabold text-blue-900 mb-8 text-center shadow-sm"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          OCT Image Classification
        </motion.h1>

        {/* Model Selection */}
        <div className="mb-6">
          <label className="block text-gray-800 font-semibold mb-2">Select Model</label>
          <select
            value={selectedModel}
            onChange={(e) => setSelectedModel(e.target.value)}
            className="w-full p-3 border rounded-lg bg-white shadow-md focus:outline-none focus:ring-2 focus:ring-blue-600"
          >
            {models.map((model) => (
              <option key={model.value} value={model.value}>
                {model.label}
              </option>
            ))}
          </select>
          <p className="text-sm text-gray-700 mt-2">
            {models.find((m) => m.value === selectedModel)?.description}
          </p>
        </div>

        {/* Image Upload */}
        <div className="mb-6">
          <label className="block text-gray-800 font-semibold mb-2">Upload OCT Image</label>
          <div className="border-2 border-dashed border-blue-400 rounded-lg p-8 text-center bg-white hover:border-blue-600 transition-all duration-300">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
              id="image-upload"
            />
            <label htmlFor="image-upload" className="cursor-pointer">
              <Upload className="mx-auto h-12 w-12 text-blue-600 mb-3" />
              <p className="text-lg font-semibold text-blue-700">Click or drag image to upload</p>
            </label>
          </div>
        </div>

        {/* Loading State */}
        {isLoading && (
          <div className="text-center py-4">
            <div className="animate-spin rounded-full h-10 w-10 border-b-4 border-blue-600 mx-auto"></div>
            <p className="mt-2 text-blue-800 font-medium">Processing...</p>
          </div>
        )}

        {/* Error Message */}
        {error && (
          <div className="bg-red-200 border border-red-500 text-red-800 px-4 py-3 rounded-lg mb-6">
            {error}
          </div>
        )}

        {/* Results */}
        {selectedImage && !isLoading && (
          <motion.div
            className="grid md:grid-cols-2 gap-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <div>
              <h3 className="text-lg font-bold text-blue-800 mb-2">Original Image</h3>
              <img
                src={selectedImage}
                alt="Uploaded OCT"
                className="rounded-lg shadow-md w-full border border-gray-300"
              />
            </div>
            {prediction && (
              <div className="bg-blue-50 p-6 rounded-lg shadow-md border border-gray-200">
                <h3 className="text-lg font-bold text-blue-800 mb-4">Results</h3>
                <div className="space-y-3">
                  <p className="text-gray-700">
                    <span className="font-semibold text-blue-900">Condition:</span>{' '}
                    <span className="text-blue-700">{prediction.class}</span>
                  </p>
                  <p className="text-gray-700">
                    <span className="font-semibold text-blue-900">Confidence:</span>{' '}
                    <span className="text-blue-700">{(prediction.confidence).toFixed(2)}%</span>
                  </p>
                  {prediction.gradcam && (
                    <div className="mt-4">
                      <h4 className="font-semibold text-blue-800 mb-2">Grad-CAM Visualization</h4>
                      <img
                        src={`data:image/png;base64,${prediction.gradcam}`}
                        alt="Grad-CAM"
                        className="rounded-lg w-full border border-gray-300"
                      />
                    </div>
                  )}
                </div>
              </div>
            )}
          </motion.div>
        )}

        {/* Reset Button */}
        {selectedImage && !isLoading && (
          <div className="text-center mt-6">
            <button
              onClick={handleReset}
              className="bg-gray-600 hover:bg-gray-700 text-white px-6 py-2 rounded-lg flex items-center justify-center mx-auto transition-all duration-300"
            >
              <RefreshCw className="mr-2" /> Reset
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ClassificationPage;
