import React, { useState } from 'react';
import { classifyImage } from '../../utils/api';
import GradCAMVisualization from './GradCAMVisualization';
import LoadingSpinner from '../layout/LoadingSpinner';

const OCTClassifier = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreview(URL.createObjectURL(file));
      setResult(null);
      setError(null);
    }
  };

  const handleSubmit = async () => {
    if (!selectedFile) {
      setError('Please select an image first');
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await classifyImage(selectedFile);
      setResult(response);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="mb-4">
        <input
          type="file"
          accept="image/*"
          onChange={handleFileSelect}
          className="mb-4"
        />

        {preview && (
          <div className="mb-4">
            <h3 className="text-lg font-bold mb-2">Selected Image</h3>
            <img src={preview} alt="Preview" className="max-w-md rounded shadow" />
          </div>
        )}

        <button
          onClick={handleSubmit}
          disabled={!selectedFile || loading}
          className={`px-4 py-2 rounded ${
            loading ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600'
          } text-white`}
        >
          {loading ? <LoadingSpinner /> : 'Classify Image'}
        </button>
      </div>

      {error && (
        <div className="text-red-500 mb-4">
          Error: {error}
        </div>
      )}

      {result && (
        <div className="results">
          <h3 className="text-lg font-bold mb-2">Results</h3>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <p className="font-bold">Class: {result.class}</p>
              <p>Confidence: {result.confidence.toFixed(2)}%</p>
            </div>
            {result.gradcam && (
              <GradCAMVisualization
                original={result.original}
                gradcam={result.gradcam}
              />
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default OCTClassifier;
