import React from 'react';

const GradCAMVisual = ({ classificationResult }) => {
  if (!classificationResult) return null;

  const getExplanation = (className) => {
    const explanations = {
      'CNV': 'Choroidal Neovascularization (CNV) is characterized by abnormal blood vessel growth from the choroid into the retina.',
      'DME': 'Diabetic Macular Edema (DME) is a condition where fluid accumulates in the macula due to leaking blood vessels.',
      'DRUSEN': 'Drusen are yellow deposits under the retina, often associated with age-related macular degeneration.',
      'NORMAL': 'No pathological findings detected in the OCT scan.'
    };
    return explanations[className] || 'No explanation available';
  };

  const getRecommendations = (className) => {
    const recommendations = {
      'CNV': [
        'Immediate referral to retina specialist',
        'Consider anti-VEGF therapy',
        'Regular monitoring required'
      ],
      'DME': [
        'Blood sugar control',
        'Regular ophthalmological check-ups',
        'Consider laser treatment or anti-VEGF therapy'
      ],
      'DRUSEN': [
        'Regular monitoring',
        'Lifestyle modifications (smoking cessation, healthy diet)',
        'AREDS supplements if recommended'
      ],
      'NORMAL': [
        'Regular eye check-ups',
        'Maintain healthy lifestyle',
        'Report any vision changes promptly'
      ]
    };
    return recommendations[className] || [];
  };

  return (
    <div className="card p-6 bg-white rounded-lg shadow-lg mt-6">
      <h2 className="text-2xl font-bold mb-4">AI Explanation</h2>
      
      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">Diagnosis Explanation:</h3>
        <p className="text-gray-700">{getExplanation(classificationResult.class)}</p>
      </div>

      <div className="mb-6">
        <h3 className="text-xl font-semibold mb-2">Recommendations:</h3>
        <ul className="list-disc pl-5">
          {getRecommendations(classificationResult.class).map((rec, index) => (
            <li key={index} className="text-gray-700 mb-1">{rec}</li>
          ))}
        </ul>
      </div>

      <div className="bg-yellow-50 p-4 rounded-lg">
        <p className="text-sm text-yellow-800">
          <strong>Note:</strong> This is an AI-assisted diagnosis. Please consult with a healthcare professional for accurate diagnosis and treatment.
        </p>
      </div>
    </div>
  );
};

export default GradCAMVisual;
