import React, { useState } from 'react';
import { classifyImage } from '../../utils/api';  // Make sure this import is correct

const OCTClassifier = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreview(URL.createObjectURL(file));
      setError(null);
      
      try {
        setLoading(true);
        const response = await classifyImage(file);
        setResult(response);
        setError(null);
      } catch (err) {
        setError(err.message || 'Error processing image');
        setResult(null);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">OCT Image Classification</h2>
      
      <div className="mb-4">
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="hidden"
          id="file-upload"
        />
        <label
          htmlFor="file-upload"
          className="cursor-pointer bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Upload Image
        </label>
      </div>

      {error && (
        <div className="text-red-500 mb-4">
          {error}
        </div>
      )}

      {loading && (
        <div className="text-blue-500">
          Processing image...
        </div>
      )}

      {preview && (
        <div className="mb-4">
          <h3 className="text-lg font-bold">Original Image</h3>
          <img src={preview} alt="Preview" className="max-w-md rounded shadow" />
        </div>
      )}

      {result && (
        <div className="mt-4">
          <h3 className="text-lg font-bold">Results</h3>
          <p>Class: {result.class}</p>
          <p>Confidence: {result.confidence.toFixed(2)}%</p>
          {result.gradcam && (
            <div className="mt-2">
              <h4 className="font-bold">Grad-CAM Visualization</h4>
              <img
                src={`data:image/png;base64,${result.gradcam}`}
                alt="Grad-CAM"
                className="max-w-md rounded shadow"
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default OCTClassifier;
