import React, { useState } from 'react';
import GradCAMVisualization from './GradCAMVisualization';
import LoadingSpinner from '../LoadingSpinner';

const OCTClassifier = () => {
  const [selectedModel, setSelectedModel] = useState('mobilenet_v3');
  const [file, setFile] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);

  const models = [
    { value: 'mobilenet_v3', label: 'MobileNet V3' },
    { value: 'efficientnet_b0', label: 'EfficientNet B0' },
    { value: 'squeezenet1_1', label: 'SqueezeNet 1.1' },
    { value: 'resnet50', label: 'ResNet 50' },
    { value: 'resnet18', label: 'ResNet 18' },
    { value: 'mobilevit', label: 'MobileViT' }
  ];

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setFile(file);
    setLoading(true);

    const formData = new FormData();
    formData.append('file', file);
    formData.append('model', selectedModel);

    try {
      const response = await fetch('http://localhost:5000/api/classify', {
        method: 'POST',
        body: formData,
      });
      
      const data = await response.json();
      setPrediction(data);
    } catch (error) {
      console.error('Error classifying image:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mx-auto p-4 max-w-4xl">
      <Card className="p-6">
        <h2 className="text-2xl font-bold mb-4">OCT Image Classification</h2>
        
        <div className="space-y-6">
          {/* Model Selection */}
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium">Select Model</label>
            <select 
              value={selectedModel} 
              onChange={(e) => setSelectedModel(e.target.value)}
              className="border rounded p-2"
            >
              {models.map((model) => (
                <option key={model.value} value={model.value}>
                  {model.label}
                </option>
              ))}
            </select>
          </div>

          {/* File Upload */}
          <div className="flex flex-col space-y-2">
            <label className="text-sm font-medium">Upload Image</label>
            <input
              type="file"
              accept="image/*"
              onChange={handleFileUpload}
              className="border rounded p-2"
            />
          </div>

          {/* Loading State */}
          {loading && <LoadingSpinner />}

          {/* Results */}
          {prediction && (
            <div className="space-y-6">
              <div className="p-4 bg-gray-50 rounded-lg">
                <h3 className="font-bold text-lg mb-2">Results</h3>
                <p className="text-lg">Class: {prediction.class}</p>
                <p className="text-lg">Confidence: {prediction.confidence.toFixed(2)}%</p>
              </div>

              <GradCAMVisualization 
                original={prediction.original}
                gradcam={prediction.gradcam}
              />
            </div>
          )}
        </div>
      </Card>
    </div>
  );
};

export default OCTClassifier;
