import React, { useState } from 'react';
import { Upload, Info, RefreshCw } from 'lucide-react';
import { motion } from 'framer-motion';
import OCTClassifier from '../components/classification/OCTClassifier';
import GradCAMVisual from '../components/classification/GradCAMVisualization';
import { classifyImage } from '../utils/api';

const ClassificationPage = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [selectedModel, setSelectedModel] = useState('mobilenet_v3');
  const [error, setError] = useState(null);
  const [gradcamImage, setGradcamImage] = useState(null);

  const models = [
    { value: 'mobilenet_v3', label: 'MobileNet V3', description: 'Lightweight and fast, ideal for mobile devices.' },
    { value: 'efficientnet_b0', label: 'EfficientNet B0', description: 'Balanced accuracy and efficiency.' },
    { value: 'resnet18', label: 'ResNet 18', description: 'Deep residual learning for image recognition.' },
    { value: 'resnet50', label: 'ResNet 50', description: 'Improved accuracy with deeper architecture.' },
    { value: 'squeezenet1_1', label: 'SqueezeNet', description: 'Compact model with minimal parameters.' },
    { value: 'mobilevit_s', label: 'MobileViT', description: 'Combines CNNs and Transformers for mobile devices.' },
  ];

  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setSelectedImage(URL.createObjectURL(file));
    setIsLoading(true);
    setError(null);
    setGradcamImage(null);

    try {
      const result = await classifyImage(file, selectedModel);
      setPrediction(result);
      
      // Handle GradCAM data separately
      if (result.gradcam) {
        // Convert base64 to image URL if necessary
        const gradcamUrl = result.gradcam.startsWith('data:image') 
          ? result.gradcam 
          : `data:image/png;base64,${result.gradcam}`;
        setGradcamImage(gradcamUrl);
      }
    } catch (error) {
      console.error('Error:', error);
      setError(`Error processing image: ${error.message}`);
    } finally {
      setIsLoading(false);
    }
  };

  const handleReset = () => {
    setSelectedImage(null);
    setPrediction(null);
    setError(null);
    setGradcamImage(null);
  };

  return (
    <div
      className="relative bg-cover bg-center min-h-screen"
      style={{
        backgroundImage:
          "url('https://static.vecteezy.com/system/resources/thumbnails/032/838/286/small_2x/blue-eye-staring-reflecting-nature-abstract-beauty-generated-by-ai-photo.jpg')",
      }}
    >
      {/* Dark overlay for better text visibility */}
      <div className="absolute inset-0 bg-gradient-to-r from-black/70 to-black/50"></div>

      <div className="relative max-w-5xl mx-auto p-8 text-white">
        <motion.h1
          className="text-5xl font-extrabold mb-8 text-center bg-clip-text text-transparent bg-gradient-to-r from-blue-400 to-purple-400"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
        >
          OCT Image Classification
        </motion.h1>

        {/* Model Selection */}
        <motion.div
          className="mb-6 bg-white/10 backdrop-blur-sm rounded-lg p-6 shadow-2xl"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
        >
          <label className="block text-lg font-semibold mb-2">Select Model</label>
          <select
            value={selectedModel}
            onChange={(e) => setSelectedModel(e.target.value)}
            className="w-full p-3 border rounded-lg bg-white/20 text-white focus:outline-none focus:ring-2 focus:ring-blue-600"
          >
            {models.map((model) => (
              <option key={model.value} value={model.value} className="bg-gray-800">
                {model.label}
              </option>
            ))}
          </select>
          <p className="text-sm text-gray-300 mt-2">
            {models.find((m) => m.value === selectedModel)?.description}
          </p>
        </motion.div>

        {/* Image Upload */}
        <motion.div
          className="mb-6 bg-white/10 backdrop-blur-sm rounded-lg p-6 shadow-2xl"
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.4 }}
        >
          <label className="block text-lg font-semibold mb-2">Upload OCT Image</label>
          <div className="border-2 border-dashed border-blue-400 rounded-lg p-8 text-center bg-white/10 hover:border-blue-600 transition-all duration-300">
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="hidden"
              id="image-upload"
            />
            <label htmlFor="image-upload" className="cursor-pointer">
              <Upload className="mx-auto h-12 w-12 text-blue-400 mb-3" />
              <p className="text-lg font-semibold text-blue-400">Click or drag image to upload</p>
            </label>
          </div>
        </motion.div>

        {/* Loading State */}
        {isLoading && (
          <motion.div
            className="text-center py-4"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <div className="animate-spin rounded-full h-10 w-10 border-b-4 border-blue-600 mx-auto"></div>
            <p className="mt-2 text-blue-400 font-medium">Processing...</p>
          </motion.div>
        )}

        {/* Error Message */}
        {error && (
          <motion.div
            className="bg-red-200 border border-red-500 text-red-800 px-4 py-3 rounded-lg mb-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            {error}
          </motion.div>
        )}

        {/* Results */}
        {selectedImage && !isLoading && (
          <motion.div
            className="grid md:grid-cols-2 gap-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <div>
              <h3 className="text-lg font-bold text-blue-400 mb-2">Original Image</h3>
              <img
                src={selectedImage}
                alt="Uploaded OCT"
                className="rounded-lg shadow-md w-full h-64 object-cover border border-gray-300"
              />
            </div>
            {prediction && (
              <div className="bg-white/10 backdrop-blur-sm p-6 rounded-lg shadow-md border border-gray-200">
                <h3 className="text-lg font-bold text-blue-400 mb-4">Results</h3>
                <div className="space-y-3">
                  <p className="text-gray-300">
                    <span className="font-semibold text-blue-400">Condition:</span>{' '}
                    <span className="text-blue-300">{prediction.class}</span>
                  </p>
                  <p className="text-gray-300">
                    <span className="font-semibold text-blue-400">Confidence:</span>{' '}
                    <span className="text-blue-300">{(prediction.confidence * 100).toFixed(1)}%</span>
                  </p>
                  {gradcamImage && (
                    <div className="mt-4">
                      <h4 className="font-semibold text-blue-400 mb-2">Grad-CAM Visualization</h4>
                      <div className="relative h-64 w-full">
                        <img
                          src={gradcamImage}
                          alt="Grad-CAM Visualization"
                          className="rounded-lg w-full h-full object-cover border border-gray-300"
                        />
                      </div>
                    </div>
                  )}
                </div>
              </div>
            )}
          </motion.div>
        )}

        {/* Reset Button */}
        {selectedImage && !isLoading && (
          <motion.div
            className="text-center mt-6"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
          >
            <button
              onClick={handleReset}
              className="bg-gray-600 hover:bg-gray-700 text-white px-6 py-2 rounded-lg flex items-center justify-center mx-auto transition-all duration-300"
            >
              <RefreshCw className="mr-2" /> Reset
            </button>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default ClassificationPage;
