import React, { useState } from 'react';
import { classifyImage } from '../../utils/api';
import LoadingSpinner from '../../pages/LoadingSpinner';

const OCTClassifier = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [modelName, setModelName] = useState('mobilenet_v3');

  const handleFileSelect = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setSelectedFile(file);
    setPreview(URL.createObjectURL(file));
    setError(null);
    setResult(null);
    setLoading(true);

    try {
      const result = await classifyImage(file, modelName);
      setResult(result);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-700">Select Model</label>
        <select
          value={modelName}
          onChange={(e) => setModelName(e.target.value)}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
        >
          <option value="mobilenet_v3">MobileNet V3</option>
          <option value="efficientnet_b0">EfficientNet B0</option>
          <option value="resnet18">ResNet 18</option>
        </select>
      </div>

      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-700">Upload Image</label>
        <input
          type="file"
          accept="image/*"
          onChange={handleFileSelect}
          className="mt-1 block w-full"
        />
      </div>

      {loading && <LoadingSpinner />}
      
      {error && (
        <div className="text-red-600 mb-4">
          {error}
        </div>
      )}

      {preview && (
        <div className="mb-6">
          <h3 className="text-lg font-medium text-gray-900">Preview</h3>
          <img src={preview} alt="Preview" className="mt-2 max-w-md rounded shadow" />
        </div>
      )}

      {result && (
        <div className="bg-white p-6 rounded-lg shadow">
          <h3 className="text-lg font-medium text-gray-900 mb-4">Results</h3>
          <p className="text-gray-600">Class: <span className="font-medium">{result.class}</span></p>
          <p className="text-gray-600">Confidence: <span className="font-medium">{result.confidence.toFixed(2)}%</span></p>
        </div>
      )}
    </div>
  );
};

export default OCTClassifier;
