import React, { useState } from "react";
import axios from "axios";
import GradCAMVisualization from "./GradCAMVisualization";

const OCTClassifier = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [selectedModel, setSelectedModel] = useState("ResNet");
  const [prediction, setPrediction] = useState(null);
  const [gradCAM, setGradCAM] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Handle Image Selection
  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedImage(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  // Handle Model Change
  const handleModelChange = (event) => {
    setSelectedModel(event.target.value);
  };

  // Handle Classification Request
  const classifyImage = async () => {
    if (!selectedImage) {
      setError("Please upload an image first.");
      return;
    }
    
    setLoading(true);
    setError("");
    
    const formData = new FormData();
    formData.append("file", selectedImage);
    formData.append("model", selectedModel);

    try {
      const response = await axios.post("http://127.0.0.1:5000/predict", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setPrediction(response.data.prediction);
      setGradCAM(response.data.gradcam_url);
    } catch (error) {
      setError("Error processing image. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen flex flex-col items-center">
      <h2 className="text-2xl font-bold mb-4">OCT Image Classification</h2>

      {/* Image Upload */}
      <input type="file" accept="image/*" onChange={handleImageChange} className="mb-4" />
      {preview && <img src={preview} alt="Preview" className="w-48 h-48 object-contain rounded-lg mb-4" />}

      {/* Model Selection */}
      <select value={selectedModel} onChange={handleModelChange} className="mb-4 p-2 border rounded">
        <option value="ResNet">ResNet</option>
        <option value="MobileNet">MobileNet</option>
        <option value="EfficientNet">EfficientNet</option>
        <option value="VisionTransformer">Vision Transformer</option>
      </select>

      {/* Classification Button */}
      <button onClick={classifyImage} disabled={loading} className="bg-blue-500 text-white px-4 py-2 rounded">
        {loading ? "Processing..." : "Classify Image"}
      </button>

      {/* Display Prediction */}
      {error && <p className="text-red-500 mt-4">{error}</p>}
      {prediction && (
        <div className="mt-4 p-4 bg-white shadow rounded">
          <p className="font-semibold">Prediction: {prediction.class}</p>
          <p>Confidence: {prediction.confidence}%</p>
        </div>
      )}

      {/* Grad-CAM Visualization */}
      {gradCAM && <GradCAMVisualization originalImage={preview} gradCAMImage={gradCAM} />}
    </div>
  );
};

export default OCTClassifier;
