import React, { useState } from "react";

const GradCAMVisualization = ({ originalImage, gradCAMImage }) => {
  const [opacity, setOpacity] = useState(0.5);
  const [view, setView] = useState("combined");

  return (
    <div className="mt-6 p-4 bg-white shadow rounded text-center">
      <h3 className="text-lg font-bold mb-2">Grad-CAM Visualization</h3>

      {/* Toggle Buttons */}
      <div className="flex justify-center space-x-4 mb-4">
        <button onClick={() => setView("original")} className="px-4 py-2 bg-gray-300 rounded">Original</button>
        <button onClick={() => setView("gradcam")} className="px-4 py-2 bg-gray-300 rounded">Grad-CAM</button>
        <button onClick={() => setView("combined")} className="px-4 py-2 bg-gray-300 rounded">Overlay</button>
      </div>

      {/* Image Display */}
      <div className="relative w-64 h-64 mx-auto">
        {view === "original" && <img src={originalImage} alt="Original" className="w-full h-full object-cover rounded-lg" />}
        {view === "gradcam" && <img src={gradCAMImage} alt="Grad-CAM" className="w-full h-full object-cover rounded-lg" />}
        {view === "combined" && (
          <div className="relative">
            <img src={originalImage} alt="Original" className="w-full h-full object-cover rounded-lg absolute" />
            <img src={gradCAMImage} alt="Grad-CAM" className="w-full h-full object-cover rounded-lg absolute" style={{ opacity }} />
          </div>
        )}
      </div>

      {/* Opacity Control */}
      {view === "combined" && (
        <div className="mt-4">
          <label className="mr-2">Opacity:</label>
          <input type="range" min="0" max="1" step="0.1" value={opacity} onChange={(e) => setOpacity(parseFloat(e.target.value))} />
        </div>
      )}
    </div>
  );
};

export default GradCAMVisualization;
