import React, { useState } from 'react';
import axios from 'axios';

const OCTClassifier = ({ onClassificationComplete }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileSelect = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async () => {
    if (!selectedFile) {
      setError('Please select an image first');
      return;
    }

    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append('image', selectedFile); // Changed from 'file' to 'image'

    try {
      const response = await axios.post('http://localhost:8000/classify', formData, { // Removed /api/ from URL
        headers: {
          'Content-Type': 'multipart/form-data',
          'Accept': 'application/json'
        },
      });

      // Handle different response structure
      const result = {
        class: response.data.predicted_class || response.data.class,
        confidence: response.data.confidence || response.data.probability || 0
      };

      setResult(result);
      if (onClassificationComplete) {
        onClassificationComplete(result);
      }
    } catch (err) {
      console.error('Classification error:', err);
      setError('Error classifying image: ' + (err.response?.data?.message || err.response?.data?.detail || err.message));
    } finally {
      setLoading(false);
    }
  };


  return (
    <div className="card p-6 bg-white rounded-lg shadow-lg">
      <h2 className="text-2xl font-bold mb-4">OCT Image Classification</h2>
      
      <div className="mb-4">
        <input
          type="file"
          accept="image/*"
          onChange={handleFileSelect}
          className="block w-full text-sm text-gray-500
            file:mr-4 file:py-2 file:px-4
            file:rounded-full file:border-0
            file:text-sm file:font-semibold
            file:bg-blue-50 file:text-blue-700
            hover:file:bg-blue-100"
        />
      </div>

      {previewUrl && (
        <div className="mb-4">
          <img
            src={previewUrl}
            alt="Preview"
            className="max-w-full h-auto rounded-lg"
          />
        </div>
      )}

      <button
        onClick={handleSubmit}
        disabled={loading || !selectedFile}
        className={`w-full py-2 px-4 rounded-lg font-semibold text-white
          ${loading || !selectedFile 
            ? 'bg-gray-400 cursor-not-allowed'
            : 'bg-blue-600 hover:bg-blue-700'}`}
      >
        {loading ? 'Classifying...' : 'Classify Image'}
      </button>

      {error && (
        <div className="mt-4 p-4 bg-red-50 text-red-700 rounded-lg">
          {error}
        </div>
      )}

      {result && (
        <div className="mt-4 p-4 bg-green-50 rounded-lg">
          <h3 className="font-semibold text-lg mb-2">Results:</h3>
          <p>Class: <span className="font-bold">{result.class}</span></p>
          <p>Confidence: <span className="font-bold">{(result.confidence * 100).toFixed(2)}%</span></p>
        </div>
      )}
    </div>
  );
};

export default OCTClassifier;
