// src/components/chat/Chatbot.jsx
import React, { useState } from 'react';
import { Send, Bot } from 'lucide-react';

const Chatbot = ({ apiKey }) => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages((prev) => [...prev, userMessage]);
    setInput('');
    setIsLoading(true);

    try {
      const response = await fetch('https://api.deepseek.com/v1/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${apiKey}`,
        },
        body: JSON.stringify({
          model: 'deepseek-chat',
          messages: [{ role: 'user', content: input }],
        }),
      });

      const data = await response.json();
      const botMessage = { text: data.choices[0].message.content, sender: 'bot' };
      setMessages((prev) => [...prev, botMessage]);
    } catch (error) {
      console.error('Error:', error);
      setMessages((prev) => [
        ...prev,
        { text: 'Failed to get a response. Please try again.', sender: 'bot' },
      ]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="fixed bottom-4 right-4 w-96 bg-white/10 backdrop-blur-sm rounded-lg shadow-lg border border-gray-700">
      <div className="p-4">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-blue-400 flex items-center">
            <Bot className="mr-2" /> Retinal Disease Chatbot
          </h3>
        </div>
        <div className="h-64 overflow-y-auto mb-4">
          {messages.map((msg, index) => (
            <div
              key={index}
              className={`mb-2 p-2 rounded-lg ${
                msg.sender === 'user' ? 'bg-blue-600/20 ml-auto' : 'bg-gray-700/20'
              }`}
            >
              <p className="text-sm text-white">{msg.text}</p>
            </div>
          ))}
          {isLoading && (
            <div className="flex justify-center">
              <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-400"></div>
            </div>
          )}
        </div>
        <div className="flex">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            className="flex-1 p-2 rounded-l-lg bg-gray-700/20 text-white focus:outline-none"
            placeholder="Ask about retinal diseases..."
          />
          <button
            onClick={handleSendMessage}
            className="p-2 bg-blue-600 rounded-r-lg hover:bg-blue-700 transition-all duration-300"
          >
            <Send className="h-5 w-5 text-white" />
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chatbot;
